import React, { useState, useRef, useEffect } from 'react';
import Header from '../components/Header';
import './SmartAssistant.css';
import { S3Client, GetObjectCommand } from '@aws-sdk/client-s3';

const s3Client = new S3Client({
  region: 'US West (N. California) us-west-1',
  credentials: {
    accessKeyId: 'AKIAZ5TC4W7JUSUTOQVR',
    secretAccessKey: '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'
  }
});

function SmartAssistant() {
  const [question, setQuestion] = useState("");
  const [response, setResponse] = useState("");
  const [summary, setSummary] = useState("Loading summary...");
  const [isRecording, setIsRecording] = useState(false);
  const [audioBlob, setAudioBlob] = useState(null);
  const mediaRecorderRef = useRef(null);
  const audioURL = audioBlob ? URL.createObjectURL(audioBlob) : null;

  useEffect(() => {
    const fetchSummaryFromS3 = async () => {
      const bucketName = 'YOUR_BUCKET_NAME';
      const summaryKey = 'uploads/YOUR_FILE_NAME_summary.txt'; // Adjust this path to match your setup

      try {
        const command = new GetObjectCommand({
          Bucket: bucketName,
          Key: summaryKey
        });

        const summaryObject = await s3Client.send(command);
        const summaryText = await streamToString(summaryObject.Body);
        setSummary(summaryText);
      } catch (error) {
        console.error("Error fetching summary:", error);
        setSummary("Failed to load summary. Please try again.");
      }
    };

    fetchSummaryFromS3();
  }, []);

  const streamToString = (stream) => {
    const reader = stream.getReader();
    const decoder = new TextDecoder("utf-8");
    let result = "";

    return reader.read().then(function processText({ done, value }) {
      if (done) {
        return result;
      }
      result += decoder.decode(value, { stream: true });
      return reader.read().then(processText);
    });
  };

  const handleSubmit = () => {
    if (audioBlob) {
      setResponse("This is a sample response to your recorded question."); // Placeholder response for audio
    } else if (question) {
      setResponse("This is a sample response to your typed question."); // Placeholder response for text
    }
  };

  const handleAudioSubmit = () => {
    if (isRecording) {
      mediaRecorderRef.current.stop();
      setIsRecording(false);
    } else {
      navigator.mediaDevices.getUserMedia({ audio: true }).then((stream) => {
        const mediaRecorder = new MediaRecorder(stream);
        mediaRecorderRef.current = mediaRecorder;
        mediaRecorder.start();
        setIsRecording(true);

        const audioChunks = [];
        mediaRecorder.ondataavailable = (event) => {
          audioChunks.push(event.data);
        };

        mediaRecorder.onstop = () => {
          const audioBlob = new Blob(audioChunks, { type: 'audio/wav' });
          setAudioBlob(audioBlob);
          setIsRecording(false);
        };
      }).catch((error) => {
        console.error("Microphone access denied or error: ", error);
      });
    }
  };

  const handleClearAudio = () => {
    setAudioBlob(null);
    setQuestion(""); // Reset text input when returning to text input mode
  };

  return (
    <div className="smart-assistant-page">
      <Header />
      <h2>Smart Assistant Summary</h2>
      <div className="summary">
        {summary}
      </div>

      <h3>Ask a Question</h3>
      <div className="question-box">
        {audioBlob ? (
          <div className="audio-playback">
            <audio controls src={audioURL} />
            <button onClick={handleClearAudio} className="close-audio-button">âœ–</button>
          </div>
        ) : !isRecording ? (
          <textarea
            placeholder="Type your question here or use the mic..."
            value={question}
            onChange={(e) => setQuestion(e.target.value)}
          />
        ) : (
          <div className="recording-indicator">Recording... ðŸ”´</div>
        )}
        <div className="question-box-buttons">
          <button onClick={handleAudioSubmit} className={`mic-button ${isRecording ? 'recording' : ''}`}>
            {isRecording ? "Stop Recording" : "ðŸŽ¤ Use Mic"}
          </button>
          <button 
            onClick={handleSubmit} 
            className="send-button" 
            disabled={!question.trim() && !audioBlob}
          >
            Send
          </button>
        </div>
      </div>

      <div className="response-box">
        <h4>Assistant Response</h4>
        <p>{response}</p>
      </div>
    </div>
  );
}

export default SmartAssistant;
