import React, { useState } from 'react';
import './UploadNotes.css';

function UploadNotes() {
  // Mock folder structure
  const folders = [
    { name: "Math", files: [] },
    { name: "Science", files: [] },
    { name: "History", files: [] },
    { name: "Literature", files: [] },
  ];

  const [folderData, setFolderData] = useState(folders);
  const [selectedFolder, setSelectedFolder] = useState(folders[0].name);

  const handleFileUpload = (event) => {
    const uploadedFiles = Array.from(event.target.files).map(file => file.name);

    // Add files to the selected folder
    setFolderData(prevData => 
      prevData.map(folder => 
        folder.name === selectedFolder 
          ? { ...folder, files: [...folder.files, ...uploadedFiles] } 
          : folder
      )
    );
  };

  return (
    <div className="upload-notes-page">
      <div className="sidebar">
        <h3>Folders</h3>
        <ul className="folder-list">
          {folderData.map(folder => (
            <li 
              key={folder.name} 
              className={folder.name === selectedFolder ? "selected" : ""}
              onClick={() => setSelectedFolder(folder.name)}
            >
              {folder.name}
            </li>
          ))}
        </ul>
      </div>

      <div className="content">
        <h3>{selectedFolder} Notes</h3>
        <input 
          type="file" 
          multiple 
          onChange={handleFileUpload} 
          className="file-input" 
        />

        <div className="file-list">
          <h4>Files:</h4>
          <ul>
            {folderData
              .find(folder => folder.name === selectedFolder)
              .files.map((fileName, index) => (
                <li key={index}>{fileName}</li>
              ))
            }
          </ul>
        </div>
      </div>
    </div>
  );
}

export default UploadNotes;
