import React, { useState } from 'react';
import './UploadNotes.css';

function UploadNotes() {
  const [folderData, setFolderData] = useState([
    { name: "Math", files: [], subfolders: [] },
    { name: "Science", files: [], subfolders: [] },
  ]);

  const [selectedFolder, setSelectedFolder] = useState("Math");
  const [editingFolder, setEditingFolder] = useState(null);
  const [editName, setEditName] = useState("");
  const [showMenu, setShowMenu] = useState(null);

  // Handle file upload for a specific folder
  const handleFileUpload = (event, folderName) => {
    const uploadedFiles = Array.from(event.target.files).map((file) => file.name);
    setFolderData((prevData) =>
      prevData.map((folder) =>
        folder.name === folderName
          ? { ...folder, files: [...folder.files, ...uploadedFiles] }
          : folder
      )
    );
    setShowMenu(null); // Close menu after uploading
  };

  // Add a subfolder to a specific folder
  const handleAddSubfolder = (folderName) => {
    setFolderData((prevData) =>
      prevData.map((folder) =>
        folder.name === folderName
          ? { ...folder, subfolders: [...folder.subfolders, { name: "New Subfolder", files: [] }] }
          : folder
      )
    );
    setShowMenu(null); // Close menu after adding subfolder
  };

  // Rename a folder
  const handleEditFolder = (folderName) => {
    setEditingFolder(folderName);
    setEditName(folderName);
    setShowMenu(null); // Close menu when renaming
  };

  const handleSaveEdit = () => {
    setFolderData((prevData) =>
      prevData.map((folder) =>
        folder.name === editingFolder ? { ...folder, name: editName } : folder
      )
    );
    setEditingFolder(null);
  };

  // Delete a folder
  const handleDeleteFolder = (folderName) => {
    setFolderData((prevData) => prevData.filter((folder) => folder.name !== folderName));
    if (selectedFolder === folderName) setSelectedFolder(folderData[0]?.name || "");
    setShowMenu(null); // Close menu after deleting
  };

  // Toggle menu visibility
  const toggleMenu = (folderName) => {
    setShowMenu(showMenu === folderName ? null : folderName);
  };

  return (
    <div className="upload-notes-page">
      <div className="sidebar">
        <h3>Folders</h3>
        <ul className="folder-list">
          {folderData.map((folder) => (
            <li key={folder.name}>
              <div className="folder-header" onClick={() => setSelectedFolder(folder.name)}>
                <span>{folder.name}</span>
                <div className="menu-container">
                  <span className="menu-icon" onClick={() => toggleMenu(folder.name)}>â‹®</span>
                  {showMenu === folder.name && (
                    <div className="dropdown-menu">
                      <button onClick={() => handleEditFolder(folder.name)}>Rename</button>
                      <button onClick={() => handleAddSubfolder(folder.name)}>Create New Subfolder</button>
                      <button onClick={() => document.getElementById(`file-upload-${folder.name}`).click()}>
                        Upload File
                      </button>
                      <button onClick={() => handleDeleteFolder(folder.name)}>Delete</button>
                      <input
                        type="file"
                        multiple
                        id={`file-upload-${folder.name}`}
                        style={{ display: "none" }}
                        onChange={(e) => handleFileUpload(e, folder.name)}
                      />
                    </div>
                  )}
                </div>
              </div>

              {/* Display files under each folder */}
              <ul className="file-list">
                {folder.files.map((fileName, index) => (
                  <li key={index} className="file-item">{fileName}</li>
                ))}
              </ul>

              {/* Display subfolders */}
              <ul className="subfolder-list">
                {folder.subfolders.map((subfolder, index) => (
                  <li key={index} className="subfolder-item">
                    <span>{subfolder.name}</span>
                  </li>
                ))}
              </ul>

              {editingFolder === folder.name && (
                <input
                  type="text"
                  value={editName}
                  onChange={(e) => setEditName(e.target.value)}
                  onBlur={handleSaveEdit}
                  autoFocus
                />
              )}
            </li>
          ))}
        </ul>

        {/* Add Folder (not used directly for uploading but can create new main folders) */}
        <div className="add-folder">
          <input
            type="text"
            placeholder="New Folder Name"
            value={editName}
            onChange={(e) => setEditName(e.target.value)}
          />
          <button onClick={() => setFolderData([...folderData, { name: editName, files: [], subfolders: [] }])}>
            Add Folder
          </button>
        </div>
      </div>

      <div className="content">
        <h3>{selectedFolder} Notes</h3>
        <p>Select options in the sidebar to manage files and folders.</p>
      </div>
    </div>
  );
}

export default UploadNotes;
