// SmartAssistant.js

import React, { useState, useRef, useEffect } from 'react';
import { useLocation } from 'react-router-dom';
import Header from '../components/Header';
import './SmartAssistant.css';

function SmartAssistant() {
  const location = useLocation();
  const uploadedFile = location.state?.uploadedFile;
  const [question, setQuestion] = useState("");
  const [response, setResponse] = useState("");
  const [isRecording, setIsRecording] = useState(false);
  const [audioBlob, setAudioBlob] = useState(null);
  const mediaRecorderRef = useRef(null);
  const audioURL = audioBlob ? URL.createObjectURL(audioBlob) : null;
  const [summary, setSummary] = useState("");

  useEffect(() => {
    if (uploadedFile === "MLP.pdf") {
      setSummary("aaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaa");
    } else if (uploadedFile === "rt1.pdf") {
      setSummary("bbbbbbbbbbbbbbbbbbbbbbbbbbbbbbbbbbbbbbb");
    } else if (uploadedFile === "https://arxiv.org/pdf/2406.09246") {
      setSummary("cccccccccccccccccccccccccccccccc");
    } else if (uploadedFile === "https://arxiv.org/pdf/2312.04560") {
      setSummary("ddddddddddddddddddddddddd");
    } else {
      setSummary("This is a brief summary of the paper's content. (Placeholder text)");
    }
  }, [uploadedFile]);

  const handleSubmit = () => {
    let formattedResponse;
    if (audioBlob) {
      const simulatedTranscription = "audio question transcription";
      formattedResponse = `Question: ${simulatedTranscription}\nAnswer: Thank you for your question! Normally, our system would dynamically search through a sophisticated Retrieval-Augmented Generation (RAG) model, designed to tap into vast knowledge sources, sift through the most relevant information, and deliver an expert answer. But since we only started trying to master AWS yesterday (and found out itâ€™s not exactly a 'learn it overnight' kind of thing), our current setup is a bit... 'manual.' Rest assured, though, once our RAG model is fully operational, your answers will come with speed, precision, and zero cloud confusion!`;
    } else if (question.trim()) {
      formattedResponse = `Question: ${question}\nAnswer: Thank you for your question! Normally, our system would dynamically search through a sophisticated Retrieval-Augmented Generation (RAG) model, designed to tap into vast knowledge sources, sift through the most relevant information, and deliver an expert answer. But since we only started trying to master AWS yesterday (and found out itâ€™s not exactly a 'learn it overnight' kind of thing), our current setup is a bit... 'manual.' Rest assured, though, once our RAG model is fully operational, your answers will come with speed, precision, and zero cloud confusion!`;
    } else {
      formattedResponse = "No question provided.";
    }
    setResponse(formattedResponse);
  };

  const handleAudioSubmit = async () => {
    if (!navigator.mediaDevices || !navigator.mediaDevices.getUserMedia) {
      console.error("getUserMedia is not supported in this browser or context.");
      setResponse("Error: Unable to access microphone. Please check your browser settings or try another browser.");
      return;
    }

    try {
      const permission = await navigator.permissions.query({ name: "microphone" });
      if (permission.state === "denied") {
        setResponse("Microphone access is denied. Please enable it in your browser settings.");
        return;
      }

      if (isRecording) {
        mediaRecorderRef.current.stop();
        setIsRecording(false);
      } else {
        const stream = await navigator.mediaDevices.getUserMedia({ audio: true });
        const mediaRecorder = new MediaRecorder(stream);
        mediaRecorderRef.current = mediaRecorder;
        mediaRecorder.start();
        setIsRecording(true);

        const audioChunks = [];
        mediaRecorder.ondataavailable = (event) => {
          audioChunks.push(event.data);
        };

        mediaRecorder.onstop = () => {
          const audioBlob = new Blob(audioChunks, { type: 'audio/wav' });
          setAudioBlob(audioBlob);
          setIsRecording(false);
        };
      }
    } catch (error) {
      console.error("Microphone access denied or error: ", error);
      setResponse("Error: Microphone access denied or an error occurred. Please check permissions and try again.");
    }
  };

  const handleClearAudio = () => {
    setAudioBlob(null);
    setQuestion(""); // Reset text input when returning to text input mode
  };

  return (
    <div className="smart-assistant-page">
      <Header />
      <h2>Smart Assistant Summary</h2>
      <div className="summary">
        {summary}
      </div>

      <h3>Ask a Question</h3>
      <div className="question-box">
        {audioBlob ? (
          <div className="audio-playback">
            <audio controls src={audioURL} />
            <button onClick={handleClearAudio} className="close-audio-button">âœ–</button>
          </div>
        ) : !isRecording ? (
          <textarea
            placeholder="Type your question here or use the mic..."
            value={question}
            onChange={(e) => setQuestion(e.target.value)}
          />
        ) : (
          <div className="recording-indicator">Recording... ðŸ”´</div>
        )}
        <div className="question-box-buttons">
          <button onClick={handleAudioSubmit} className={`mic-button ${isRecording ? 'recording' : ''}`}>
            {isRecording ? "Stop Recording" : "ðŸŽ¤ Use Mic"}
          </button>
          <button 
            onClick={handleSubmit} 
            className="send-button" 
            disabled={!question.trim() && !audioBlob}
          >
            Send
          </button>
        </div>
      </div>

      <div className="response-box">
        <h4>Assistant Response</h4>
        <p className="response-text">{response}</p>
      </div>
    </div>
  );
}

export default SmartAssistant;
