// AddTask.js
import React, { useState } from 'react';
import Calendar from 'react-calendar';
import 'react-calendar/dist/Calendar.css';
import './AddTask.css';

function AddTask() {
  const [taskName, setTaskName] = useState('');
  const [deadline, setDeadline] = useState('');
  const [priority, setPriority] = useState('medium');
  const [difficulty, setDifficulty] = useState('medium');
  const [tasks, setTasks] = useState([]);
  const [view, setView] = useState('month'); // 'day', 'week', 'month'
  const [selectedDateTasks, setSelectedDateTasks] = useState([]);
  const [isDayView, setIsDayView] = useState(false);

  const handleAddTask = (e) => {
    e.preventDefault();

    const selectedDate = new Date(deadline);
    const today = new Date();
    today.setHours(0, 0, 0, 0);

    if (selectedDate <= today) {
      alert("Please select a future date for the task deadline.");
      return;
    }

    const newTask = { id: Date.now(), taskName, deadline: selectedDate, priority, difficulty };
    setTasks([...tasks, newTask]);

    setTaskName('');
    setDeadline('');
    setPriority('medium');
    setDifficulty('medium');

    scheduleDailyReminders(newTask);
  };

  const scheduleDailyReminders = (task) => {
    const intervalId = setInterval(() => {
      const today = new Date();
      const taskDeadline = new Date(task.deadline);

      if (today >= taskDeadline) {
        clearInterval(intervalId);
      } else {
        alert(`Reminder: Work on "${task.taskName}". Deadline is approaching!`);
      }
    }, 86400000); // 24 hours in milliseconds
  };

  const deleteTask = (taskId) => {
    setTasks(tasks.filter(task => task.id !== taskId));
  };

  const showDayTasks = (date) => {
    const tasksForSelectedDate = tasks.filter(task => task.deadline.toDateString() === date.toDateString());
    setSelectedDateTasks(tasksForSelectedDate);
    setIsDayView(true);
  };

  const tileContent = ({ date }) => {
    const dayTasks = tasks.filter(
      (task) => task.deadline.toDateString() === date.toDateString()
    );

    return (
      <ul className="task-list">
        {dayTasks.map((task) => (
          <li key={task.id} className={`task-${task.priority}`}>
            <span className="task-indicator">{task.taskName}</span>
            <button className="delete-task" onClick={() => deleteTask(task.id)}>✕</button>
          </li>
        ))}
      </ul>
    );
  };

  const todayDate = new Date().toISOString().split("T")[0];

  return (
    <div className="schedule-assist-page">
      <h2 className="page-title">Schedule Assist</h2>

      <form className="task-form-fullwidth" onSubmit={handleAddTask}>
        <input
          type="text"
          placeholder="Enter Task Name"
          value={taskName}
          onChange={(e) => setTaskName(e.target.value)}
          required
        />
        <input
          type="date"
          value={deadline}
          onChange={(e) => setDeadline(e.target.value)}
          required
          min={todayDate}
        />
        <select value={priority} onChange={(e) => setPriority(e.target.value)}>
          <option value="low">Low Priority</option>
          <option value="medium">Medium Priority</option>
          <option value="high">High Priority</option>
        </select>
        <select value={difficulty} onChange={(e) => setDifficulty(e.target.value)}>
          <option value="easy">Easy Difficulty</option>
          <option value="medium">Medium Difficulty</option>
          <option value="hard">Hard Difficulty</option>
        </select>
        <button type="submit">Add Task</button>
      </form>

      {/* Calendar View Toggle Buttons */}
      <div className="calendar-view-toggle">
        <button onClick={() => setView('day')}>Day</button>
        <button onClick={() => setView('week')}>Week</button>
        <button onClick={() => setView('month')}>Month</button>
      </div>

      {/* Calendar or Day View */}
      {!isDayView ? (
        <div className="calendar-container">
          <Calendar
            tileContent={tileContent}
            view={view}
            onClickDay={(date) => showDayTasks(date)}
            className="calendar"
          />
        </div>
      ) : (
        <div className="day-view">
          <h3>Tasks for {selectedDateTasks[0]?.deadline.toDateString()}</h3>
          <ul className="task-list">
            {selectedDateTasks.map((task) => (
              <li key={task.id} className={`task-${task.priority}`}>
                <span>{task.taskName}</span>
                <button className="delete-task" onClick={() => deleteTask(task.id)}>✕</button>
              </li>
            ))}
          </ul>
          <button onClick={() => setIsDayView(false)}>Back to Calendar</button>
        </div>
      )}
    </div>
  );
}

export default AddTask;
