          audioChunks.push(event.data);
        };

        mediaRecorder.onstop = () => {
          const audioBlob = new Blob(audioChunks, { type: 'audio/wav' });
          setAudioBlob(audioBlob);
          setIsRecording(false);

          // Simulate audio transcription
          const simulatedTranscription = "audio question transcription";
          const formattedResponse = `Question: ${simulatedTranscription}\nAnswer: Thank you for your question! Normally, our system would dynamically search through a sophisticated Retrieval-Augmented Generation (RAG) model, designed to tap into vast knowledge sources, sift through the most relevant information, and deliver an expert answer. But since we only started trying to master AWS yesterday (and found out itâ€™s not exactly a 'learn it overnight' kind of thing), our current setup is a bit... 'manual.' Rest assured, though, once our RAG model is fully operational, your answers will come with speed, precision, and zero cloud confusion!`;
          setResponse(formattedResponse);
        };
      }
    } catch (error) {
      console.error("Microphone access denied or error: ", error);
      setResponse("Error: Microphone access denied or an error occurred. Please check permissions and try again.");
    }
  };

  const handleClearAudio = () => {
    setAudioBlob(null);
    setQuestion(""); // Reset text input when returning to text input mode
  };

  return (
    <div className="smart-assistant-page">
      <Header />
      <h2>Smart Assistant Summary</h2>
      <div className="summary">
        {summary}
      </div>

      <h3>Ask a Question</h3>
      <div className="question-box">
        {audioBlob ? (
          <div className="audio-playback">
            <audio controls src={audioURL} />
            <button onClick={handleClearAudio} className="close-audio-button">âœ–</button>
          </div>
        ) : !isRecording ? (
          <textarea
            placeholder="Type your question here or use the mic..."
            value={question}
            onChange={(e) => setQuestion(e.target.value)}
          />
        ) : (
          <div className="recording-indicator">Recording... ðŸ”´</div>
        )}
        <div className="question-box-buttons">
          <button onClick={handleAudioSubmit} className={`mic-button ${isRecording ? 'recording' : ''}`}>
            {isRecording ? "Stop Recording" : "ðŸŽ¤ Use Mic"}
          </button>
          <button 
            onClick={handleSubmit} 
            className="send-button" 
            disabled={!question.trim() && !audioBlob}
          >
            Send
          </button>
        </div>
      </div>

      <div className="response-box">
        <h4>Assistant Response</h4>
        <p className="response-text">{response}</p>
      </div>
    </div>
  );
}

export default SmartAssistant;
