import React, { useState } from 'react';
import './SmartAssistant.css';

function SmartAssistant() {
  const [question, setQuestion] = useState("");
  const [response, setResponse] = useState("");
  const [isRecording, setIsRecording] = useState(false);

  const handleTextSubmit = () => {
    // Handle text-based question
    setResponse("This is a sample response to your question."); // Placeholder response
  };

  const handleAudioSubmit = () => {
    // Handle audio-based question (record audio, process, and get response)
    setIsRecording(!isRecording);
    setResponse("Processing audio input..."); // Placeholder for audio processing
  };

  return (
    <div className="smart-assistant-page">
      <h2>Smart Assistant Summary</h2>
      <div className="summary">
        This is a brief summary of the paper's content. (Placeholder text)
      </div>

      <h3>Ask a Question</h3>
      <div className="question-box">
        <textarea
          placeholder="Type your question here..."
          value={question}
          onChange={(e) => setQuestion(e.target.value)}
        />
        <button onClick={handleTextSubmit}>Submit</button>
        <button onClick={handleAudioSubmit}>
          {isRecording ? "Stop Recording" : "Record Audio"}
        </button>
      </div>

      <div className="response-box">
        <h4>Assistant Response</h4>
        <p>{response}</p>
      </div>
    </div>
  );
}

export default SmartAssistant;
