import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './GoogleDriveNotes.css';

function GoogleDriveNotes() {
  const [fileTree, setFileTree] = useState([]);
  const [selectedFile, setSelectedFile] = useState(null);
  const [fileContent, setFileContent] = useState('');
  const [isSidebarVisible, setIsSidebarVisible] = useState(true);

  useEffect(() => {
    fetchFileTree();
  }, []);

  const fetchFileTree = async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/files');
      setFileTree(response.data);
    } catch (error) {
      console.error('Error fetching file tree:', error);
    }
  };

  const fetchFileContent = async (filePath) => {
    try {
      setSelectedFile(filePath);
      const mimeType = getMimeType(filePath);

      if (mimeType.startsWith('text/')) {
        // For text files, load as text
        const response = await axios.get(`http://localhost:5000/api/files/content?path=${filePath}`, {
          responseType: 'text'
        });
        setFileContent(response.data);
      } else {
        // For binary files like images, PDFs, and videos, load as blob
        const response = await axios.get(`http://localhost:5000/api/files/content?path=${filePath}`, {
          responseType: 'arraybuffer'
        });
        const blob = new Blob([response.data], { type: mimeType });
        setFileContent(URL.createObjectURL(blob));
      }
    } catch (error) {
      console.error('Error fetching file content:', error);
    }
  };

  const getMimeType = (filePath) => {
    const ext = filePath.split('.').pop().toLowerCase();
    switch (ext) {
      case 'jpg':
      case 'jpeg':
      case 'png':
      case 'gif':
        return 'image/' + ext;
      case 'pdf':
        return 'application/pdf';
      case 'mp4':
      case 'webm':
      case 'ogg':
        return 'video/' + ext;
      case 'txt':
      case 'md':
        return 'text/plain';
      default:
        return '';
    }
  };

  const toggleSidebar = () => {
    setIsSidebarVisible(!isSidebarVisible);
  };

  const renderFileContent = () => {
    const mimeType = getMimeType(selectedFile);

    if (mimeType.startsWith('image/')) {
      return <img src={fileContent} alt="Preview" className="file-viewer" />;
    } else if (mimeType === 'application/pdf') {
      return <embed src={fileContent} type="application/pdf" width="100%" height="600px" />;
    } else if (mimeType.startsWith('video/')) {
      return (
        <video controls className="file-viewer">
          <source src={fileContent} type={mimeType} />
          Your browser does not support the video tag.
        </video>
      );
    } else if (mimeType.startsWith('text/')) {
      return <pre className="text-viewer">{fileContent}</pre>;
    } else {
      return <p>Unsupported file format.</p>;
    }
  };

  const renderFileTree = (tree) => {
    return tree.map((item) => (
      <div key={item.path} className="file-tree-item">
        <div
          className={`file-tree-entry ${item.type}`}
          onClick={() => item.type === 'file' ? fetchFileContent(item.path) : null}
        >
          {item.type === 'folder' ? '📁' : '📄'} {item.name}
        </div>
        {item.type === 'folder' && item.contents && (
          <div className="file-tree-contents">
            {renderFileTree(item.contents)}
          </div>
        )}
      </div>
    ));
  };

  return (
    <div className="file-browser-container">
      <button className="toggle-sidebar" onClick={toggleSidebar}>
        {isSidebarVisible ? 'Hide Sidebar' : 'Show Sidebar'}
      </button>
      {isSidebarVisible && (
        <div className="file-tree-sidebar">
          <h3>File Browser</h3>
          {renderFileTree(fileTree)}
        </div>
      )}
      <div className="file-content">
        {selectedFile ? (
          <div>
            <h3>Viewing: {selectedFile}</h3>
            {renderFileContent()}
          </div>
        ) : (
          <p>Select a file to view its content</p>
        )}
      </div>
    </div>
  );
}

export default GoogleDriveNotes;
