import React, { useState } from 'react';
import Calendar from 'react-calendar';
import 'react-calendar/dist/Calendar.css';
import './AddTask.css';

function AddTask() {
  const [taskName, setTaskName] = useState('');
  const [deadline, setDeadline] = useState('');
  const [priority, setPriority] = useState('medium');
  const [difficulty, setDifficulty] = useState('medium'); // New difficulty state
  const [tasks, setTasks] = useState([]);

  const handleAddTask = (e) => {
    e.preventDefault();

    if (!taskName || !deadline) {
      alert("Please enter both task name and deadline.");
      return;
    }

    const newTask = { taskName, deadline: new Date(deadline), priority, difficulty };
    setTasks([...tasks, newTask]);

    setTaskName('');
    setDeadline('');
    setPriority('medium');
    setDifficulty('medium');
  };

  return (
    <div className="add-task-page">
      <h2 className="page-title">Add and View Tasks</h2>
      <form className="task-form-fullwidth" onSubmit={handleAddTask}>
        <input
          type="text"
          placeholder="Enter Task Name"
          value={taskName}
          onChange={(e) => setTaskName(e.target.value)}
          required
        />
        <input
          type="date"
          value={deadline}
          onChange={(e) => setDeadline(e.target.value)}
          required
        />
        <select value={priority} onChange={(e) => setPriority(e.target.value)}>
          <option value="low">Low Priority</option>
          <option value="medium">Medium Priority</option>
          <option value="high">High Priority</option>
        </select>
        {/* New Difficulty Dropdown */}
        <select value={difficulty} onChange={(e) => setDifficulty(e.target.value)}>
          <option value="easy">Easy ifficulty</option>
          <option value="medium">Medium ifficulty</option>
          <option value="hard">Hard ifficulty</option>
        </select>
        <button type="submit">Add Task</button>
      </form>

      <div className="calendar-container">
        <Calendar className="calendar" />
      </div>
    </div>
  );
}

export default AddTask;
