// Home.js

import React, { useState } from 'react';
import AddTask from './pages/AddTask';
import OptimalPlan from './pages/OptimalPlan';
import LearningAssist from './pages/LearningAssist';
import './Home.css';

function Home() {
  const [activeTab, setActiveTab] = useState('Add Task');

  const renderTabContent = () => {
    switch (activeTab) {
      case 'Add Task':
        return <AddTask />;
      case 'Optimal Plan':
        return <OptimalPlan />;
      case 'Learning Assistant':
        return <LearningAssist />;
      default:
        return null;
    }
  };

  return (
    <div className="home-container">
      <header className="home-header">
        <h1>Study Tracker</h1>
        <p>Your tool for managing tasks, creating an optimal study plan, and learning assistance.</p>
      </header>

      <div className="tab-buttons">
        <button onClick={() => setActiveTab('Add Task')}>Add Task</button>
        {/* <button onClick={() => setActiveTab('Optimal Plan')}>Optimal Plan</button> */}
        <button onClick={() => setActiveTab('Learning Assistant')}>Learning Assistant</button>

      </div>

      <div className="tab-content">
        {renderTabContent()}
      </div>
    </div>
  );
}

export default Home;
