import React, { useState } from 'react';
import './SmartAssistant.css';

function SmartAssistant() {
  const [question, setQuestion] = useState("");
  const [response, setResponse] = useState("");
  const [isRecording, setIsRecording] = useState(false);

  const handleTextSubmit = () => {
    setResponse("This is a sample response to your question."); // Placeholder response
  };

  const handleAudioSubmit = () => {
    setIsRecording(!isRecording);
    setResponse(isRecording ? "Stopped recording." : "Recording..."); // Placeholder for audio recording state
  };

  return (
    <div className="smart-assistant-page">
      <h2>Smart Assistant Summary</h2>
      <div className="summary">
        This is a brief summary of the paper's content. (Placeholder text)
      </div>

      <h3>Ask a Question</h3>
      <div className="question-box">
        <textarea
          placeholder="Type your question here or use the mic..."
          value={question}
          onChange={(e) => setQuestion(e.target.value)}
          disabled={isRecording} // Disable typing when recording
        />
        <div className="question-box-buttons">
          <button onClick={handleAudioSubmit} className={`mic-button ${isRecording ? 'recording' : ''}`}>
            {isRecording ? "ðŸ”´ Recording..." : "ðŸŽ¤ Use Mic"}
          </button>
          <button onClick={handleTextSubmit} className="send-button">
            Send
          </button>
        </div>
      </div>

      <div className="response-box">
        <h4>Assistant Response</h4>
        <p>{response}</p>
      </div>
    </div>
  );
}

export default SmartAssistant;
