import React, { useState } from 'react';
import Header from '../components/Header';
import './GoogleDriveNotes.css';

function GoogleDriveNotes() {
  const [notes, setNotes] = useState([]);
  const [file, setFile] = useState(null);

  const handleFileChange = (event) => {
    setFile(event.target.files[0]);
  };

  const uploadNote = () => {
    if (!file) {
      alert('Please select a file to upload.');
      return;
    }
    const fileReader = new FileReader();
    fileReader.onloadend = () => {
      const newNote = {
        name: file.name,
        content: fileReader.result,
      };
      setNotes([...notes, newNote]);
      setFile(null);
      alert('File added to the local folder successfully.');
    };
    fileReader.readAsDataURL(file);
  };

  return (
    <div className="google-drive-notes-page">
      <Header />
      <div className="sidebar">
        <h3>Manage Files</h3>
        <input type="file" onChange={handleFileChange} className="file-input" />
        <button className="upload-button" onClick={uploadNote}>
          Add File to Folder
        </button>
      </div>
      <div className="content">
        <h3>Files in Folder</h3>
        <div id="notesContainer">
          {notes.length > 0 ? (
            notes.map((note, index) => (
              <div key={index} className="note">
                <span>{note.name}</span>
              </div>
            ))
          ) : (
            <p>No files in the folder.</p>
          )}
        </div>
      </div>
    </div>
  );
}

export default GoogleDriveNotes;
