import React, { useState, useEffect, useRef } from 'react';
import './UploadNotes.css';
import Header from '../components/Header';


function UploadNotes() {
  const [folderData, setFolderData] = useState([
    { name: "Math", files: [], subfolders: [] },
    { name: "Science", files: [], subfolders: [] },
  ]);

  const [mainFiles, setMainFiles] = useState([]); // New state for files outside folders
  const [selectedFolder, setSelectedFolder] = useState("Math");
  const [editingItem, setEditingItem] = useState(null);
  const [editName, setEditName] = useState("");
  const [showMenu, setShowMenu] = useState(null);
  const [showActionMenu, setShowActionMenu] = useState(false);
  const menuRef = useRef(null);

  const toggleActionMenu = () => {
    setShowActionMenu(!showActionMenu);
  };

  useEffect(() => {
    const handleClickOutside = (event) => {
      if (menuRef.current && !menuRef.current.contains(event.target)) {
        setShowMenu(null);
        setShowActionMenu(false);
      }
    };
    document.addEventListener("mousedown", handleClickOutside);
    return () => {
      document.removeEventListener("mousedown", handleClickOutside);
    };
  }, []);

  const handleFileUpload = (event, folderName = null) => {
    const uploadedFiles = Array.from(event.target.files).map((file) => file.name);

    if (folderName) {
      setFolderData((prevData) =>
        prevData.map((folder) =>
          folder.name === folderName
            ? { ...folder, files: [...folder.files, ...uploadedFiles] }
            : folder
        )
      );
    } else {
      setMainFiles([...mainFiles, ...uploadedFiles]); // Add files to main section
    }
    setShowMenu(null);
  };

  const handleAddFolder = () => {
    const newFolder = { name: "New Folder", files: [], subfolders: [] };
    setFolderData([...folderData, newFolder]);
    setEditingItem("New Folder");
    setEditName("New Folder");
    setShowActionMenu(false);
  };

  const handleAddSubfolder = (folderName) => {
    setFolderData((prevData) =>
      prevData.map((folder) =>
        folder.name === folderName
          ? { ...folder, subfolders: [...folder.subfolders, { name: "New Subfolder", files: [] }] }
          : folder
      )
    );
    setShowMenu(null);
    setEditingItem("New Subfolder");
    setEditName("New Subfolder");
  };

  const handleEditItem = (name) => {
    setEditingItem(name);
    setEditName(name);
    setShowMenu(null);
  };

  const handleSaveEdit = () => {
    setFolderData((prevData) =>
      prevData.map((folder) => {
        if (folder.name === editingItem) {
          return { ...folder, name: editName };
        } else {
          folder.subfolders = folder.subfolders.map((subfolder) =>
            subfolder.name === editingItem ? { ...subfolder, name: editName } : subfolder
          );
          folder.files = folder.files.map((file) =>
            file === editingItem ? editName : file
          );
        }
        return folder;
      })
    );
    setMainFiles((prevFiles) =>
      prevFiles.map((file) => (file === editingItem ? editName : file))
    );
    setEditingItem(null);
  };

  const handleDeleteItem = (folderName, subfolderName = null, fileName = null) => {
    if (fileName) {
      if (folderName) {
        setFolderData((prevData) =>
          prevData.map((folder) =>
            folder.name === folderName
              ? { ...folder, files: folder.files.filter((file) => file !== fileName) }
              : folder
          )
        );
      } else {
        setMainFiles(mainFiles.filter((file) => file !== fileName));
      }
    } else {
      setFolderData((prevData) =>
        prevData
          .map((folder) => {
            if (folder.name === folderName) {
              if (subfolderName) {
                folder.subfolders = folder.subfolders.filter((sub) => sub.name !== subfolderName);
              } else {
                return null;
              }
            }
            return folder;
          })
          .filter(Boolean)
      );
    }
    setShowMenu(null);
  };

  return (
    <div className="upload-notes-page">
      <Header />
      <div className="sidebar">
        <div className="sidebar-header">
          <h3>Folders</h3>
          <span className="add-icon" onClick={toggleActionMenu}>+</span>
          {showActionMenu && (
            <div className="action-menu" ref={menuRef}>
              <button onClick={handleAddFolder}>New Folder</button>
              <button onClick={() => document.getElementById('file-upload-main').click()}>Upload File</button>
              <input
                type="file"
                id="file-upload-main"
                style={{ display: "none" }}
                onChange={(e) => handleFileUpload(e)}
              />
            </div>
          )}
        </div>

        <ul className="folder-list">
          {mainFiles.map((fileName, index) => (
            <li key={index} className="file-item">
              <span>{fileName}</span>
              <div className="menu-container">
                <span className="menu-icon" onClick={() => setShowMenu(`main-${fileName}`)}>⋮</span>
                {showMenu === `main-${fileName}` && (
                  <div className="dropdown-menu" ref={menuRef}>
                    <button onClick={() => handleEditItem(fileName)}>Rename</button>
                    <button onClick={() => handleDeleteItem(null, null, fileName)}>Delete</button>
                  </div>
                )}
              </div>
            </li>
          ))}

          {folderData.map((folder) => (
            <li key={folder.name}>
              <div className="folder-header" onClick={() => setSelectedFolder(folder.name)}>
                <span>{folder.name}</span>
                <div className="menu-container">
                  <span className="menu-icon" onClick={() => setShowMenu(folder.name)}>⋮</span>
                  {showMenu === folder.name && (
                    <div className="dropdown-menu" ref={menuRef}>
                      <button onClick={() => handleEditItem(folder.name)}>Rename</button>
                      <button onClick={() => handleAddSubfolder(folder.name)}>Create Subfolder</button>
                      <button onClick={() => document.getElementById(`file-upload-${folder.name}`).click()}>
                        Upload File
                      </button>
                      <button onClick={() => handleDeleteItem(folder.name)}>Delete</button>
                      <input
                        type="file"
                        multiple
                        id={`file-upload-${folder.name}`}
                        style={{ display: "none" }}
                        onChange={(e) => handleFileUpload(e, folder.name)}
                      />
                    </div>
                  )}
                </div>
              </div>

              <ul className="file-list">
                {folder.files.map((fileName, index) => (
                  <li key={index} className="file-item">
                    <span>{fileName}</span>
                    <div className="menu-container">
                      <span className="menu-icon" onClick={() => setShowMenu(`${folder.name}-${fileName}`)}>⋮</span>
                      {showMenu === `${folder.name}-${fileName}` && (
                        <div className="dropdown-menu" ref={menuRef}>
                          <button onClick={() => handleEditItem(fileName)}>Rename</button>
                          <button onClick={() => handleDeleteItem(folder.name, null, fileName)}>Delete</button>
                        </div>
                      )}
                    </div>
                  </li>
                ))}
              </ul>

              <ul className="subfolder-list">
                {folder.subfolders.map((subfolder, index) => (
                  <li key={index} className="subfolder-item">
                    <div className="folder-header">
                      <span>{subfolder.name}</span>
                      <div className="menu-container">
                        <span className="menu-icon" onClick={() => setShowMenu(`${folder.name}-${subfolder.name}`)}>⋮</span>
                        {showMenu === `${folder.name}-${subfolder.name}` && (
                          <div className="dropdown-menu" ref={menuRef}>
                            <button onClick={() => handleEditItem(subfolder.name)}>Rename</button>
                            <button onClick={() => handleAddSubfolder(folder.name, subfolder.name)}>Create Subfolder</button>
                            <button onClick={() => document.getElementById(`file-upload-${folder.name}-${subfolder.name}`).click()}>
                              Upload File
                            </button>
                            <button onClick={() => handleDeleteItem(folder.name, subfolder.name)}>Delete</button>
                            <input
                              type="file"
                              multiple
                              id={`file-upload-${folder.name}-${subfolder.name}`}
                              style={{ display: "none" }}
                              onChange={(e) => handleFileUpload(e, folder.name, subfolder.name)}
                            />
                          </div>
                        )}
                      </div>
                    </div>
                  </li>
                ))}
              </ul>

              {editingItem === folder.name && (
                <input
                  type="text"
                  value={editName}
                  onChange={(e) => setEditName(e.target.value)}
                  onBlur={handleSaveEdit}
                  onKeyDown={(e) => e.key === 'Enter' && handleSaveEdit()}
                  autoFocus
                />
              )}
            </li>
          ))}
        </ul>
      </div>

      <div className="content">
        <h3>{selectedFolder} Notes</h3>
        <p>Select options in the sidebar to manage files and folders.</p>
      </div>
    </div>
  );
}

export default UploadNotes;
